import{_ as o,K as n,L as s,M as r,N as i,a5 as t,W as c,F as e}from"./framework-edebdfe1.js";const u={},h=r("h1",{id:"arbitrum",tabindex:"-1"},[r("a",{class:"header-anchor",href:"#arbitrum","aria-hidden":"true"},"#"),i(" Arbitrum")],-1),d={id:"https-arbiscan-io",tabindex:"-1"},p=r("a",{class:"header-anchor",href:"#https-arbiscan-io","aria-hidden":"true"},"#",-1),b={href:"https://arbiscan.io/",target:"_blank",rel:"noopener noreferrer"},m=c('<h2 id="一、历史" tabindex="-1"><a class="header-anchor" href="#一、历史" aria-hidden="true">#</a> 一、历史</h2><ol><li><p><strong>背景</strong></p><ul><li>Arbitrum 是一种 Layer 2 解决方案，旨在提高以太坊的可扩展性和交易效率。</li><li>以太坊网络的拥堵问题促使开发者寻找高效的解决方案，以降低交易费用和提高速度。</li></ul></li><li><p><strong>发展历程</strong></p><ul><li>2018 年，Offchain Labs 团队成立，开始研发 Arbitrum 项目。</li><li>2020 年，Arbitrum 测试网上线，吸引了众多开发者参与。</li><li>2021 年，Arbitrum One 主网正式推出，标志着该项目的成功。</li></ul></li></ol><h2 id="二、原理" tabindex="-1"><a class="header-anchor" href="#二、原理" aria-hidden="true">#</a> 二、原理</h2><ol><li><p><strong>Arbitrum Rollup</strong></p><ul><li>Arbitrum 采用 Rollup 技术，将多个交易打包到 Layer 2 中执行。通过将大量交易合并为一个交易提交到以太坊主链，降低了费用并提高了吞吐量。</li><li>Arbitrum Rollup 在链下执行交易，并在链上存储状态。</li></ul></li><li><p><strong>安全性</strong></p><ul><li>Arbitrum 的安全性依赖于以太坊主链，所有交易数据最终都会记录在以太坊上。</li><li>在出现争议时，Arbitrum 允许任何人验证交易的有效性，并且可以通过链上数据进行回滚和验证。</li></ul></li><li><p><strong>智能合约兼容性</strong></p><ul><li>Arbitrum 完全兼容以太坊的智能合约，开发者可以使用 Solidity 编写 DApp，并在 Arbitrum 上进行部署。</li><li>现有的以太坊工具（如 Remix 和 Truffle）也可以在 Arbitrum 上使用。</li></ul></li><li><p><strong>用户体验</strong></p><ul><li>Arbitrum 旨在提供更快、更便宜的交易体验，让用户在使用去中心化应用时拥有更好的体验。</li></ul></li></ol>',4);function _(f,A){const l=e("ExternalLinkIcon"),a=e("DocsAD");return n(),s("div",null,[h,r("h2",d,[p,i(),r("a",b,[i("https://arbiscan.io/"),t(l)])]),t(a),m])}const x=o(u,[["render",_],["__file","arbitrum.html.vue"]]);export{x as default};
