import{_ as i,K as n,L as t,a5 as l,M as a,N as e,W as r,F as h}from"./framework-edebdfe1.js";const c="/imgs/hardware/hz.png",d={},o=a("h1",{id:"通讯基础",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#通讯基础","aria-hidden":"true"},"#"),e(" 通讯基础")],-1),m=a("h2",{id:"频率",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#频率","aria-hidden":"true"},"#"),e(" 频率")],-1),p=a("p",null,"频率和波长是物理学中的两个重要概念。频率指的是单位时间内波的周期数，通常用赫兹（Hz）来表示。波长则是波在空间中传播一个周期所占据的距离，通常用米（m）来表示。频率和波长之间的关系可以用一个简单的公式来表示：频率等于光速除以波长。",-1),u=a("p",{class:"katex-block"},[a("span",{class:"katex-display"},[a("span",{class:"katex"},[a("span",{class:"katex-mathml"},[a("math",{xmlns:"http://www.w3.org/1998/Math/MathML",display:"block"},[a("semantics",null,[a("mrow",null,[a("mi",null,"f"),a("mo",null,"="),a("mi",null,"c"),a("mo",null,"÷"),a("mi",null,"l")]),a("annotation",{encoding:"application/x-tex"}," f = c \\div l ")])])]),a("span",{class:"katex-html","aria-hidden":"true"},[a("span",{class:"base"},[a("span",{class:"strut",style:{height:"0.8889em","vertical-align":"-0.1944em"}}),a("span",{class:"mord mathnormal",style:{"margin-right":"0.10764em"}},"f"),a("span",{class:"mspace",style:{"margin-right":"0.2778em"}}),a("span",{class:"mrel"},"="),a("span",{class:"mspace",style:{"margin-right":"0.2778em"}})]),a("span",{class:"base"},[a("span",{class:"strut",style:{height:"0.6667em","vertical-align":"-0.0833em"}}),a("span",{class:"mord mathnormal"},"c"),a("span",{class:"mspace",style:{"margin-right":"0.2222em"}}),a("span",{class:"mbin"},"÷"),a("span",{class:"mspace",style:{"margin-right":"0.2222em"}})]),a("span",{class:"base"},[a("span",{class:"strut",style:{height:"0.6944em"}}),a("span",{class:"mord mathnormal",style:{"margin-right":"0.01968em"}},"l")])])])])],-1),_=r('<p><img src="'+c+'" alt="er" loading="lazy"></p><h2 id="频率特性" tabindex="-1"><a class="header-anchor" href="#频率特性" aria-hidden="true">#</a> 频率特性</h2><p>高频信号是指频率高于一定阈值的信号，其通常频率范围在几十千赫兹（kHz）到几百兆赫兹（MHz）之间。高频信号的特点是波长短、传输距离短、传输速度快，常用于无线通讯、雷达、卫星通讯等领域。</p><p>低频信号则是指频率低于一定阈值的信号，其通常频率范围在几赫兹（Hz）到几十千赫兹（kHz）之间。低频信号的特点是波长长、传输距离长、传输速度慢，常用于音频信号、电力信号等领域。</p><h2 id="频段" tabindex="-1"><a class="header-anchor" href="#频段" aria-hidden="true">#</a> 频段</h2><p>频段是指一个连续电磁波的频率范围。在无线电通信中，频段通常用于划分不同的频率资源，以便于不同的通信系统之间进行区分和协调。频段的表示方法通常是用起始频率和终止频率来表示，例如，2.4GHz和5GHz就是两个常见的无线网络频段。</p><h2 id="噪声" tabindex="-1"><a class="header-anchor" href="#噪声" aria-hidden="true">#</a> 噪声</h2><h2 id="调制解调" tabindex="-1"><a class="header-anchor" href="#调制解调" aria-hidden="true">#</a> 调制解调</h2><p>调制与解调，是无线通信领域中常见的技术词汇。在发送端把基带信号（包含传输信息的有效信号）加载到某个载波（通常为高频的正弦或余弦波）的过程称为调制，得到的信号称为已调信号。解调是调制的逆过程，就是在接收端通过某种信号处理手段从已调信号中得到基带信号。</p><ul><li>AM</li><li>FM</li><li>CW</li><li>LSB</li><li>USB</li><li>WFM</li><li>DSB</li><li>RAW</li></ul><h2 id="fft" tabindex="-1"><a class="header-anchor" href="#fft" aria-hidden="true">#</a> FFT</h2><p>快速傅里叶变化</p><h2 id="滤波" tabindex="-1"><a class="header-anchor" href="#滤波" aria-hidden="true">#</a> 滤波</h2><h2 id="采样" tabindex="-1"><a class="header-anchor" href="#采样" aria-hidden="true">#</a> 采样</h2><h2 id="采样率" tabindex="-1"><a class="header-anchor" href="#采样率" aria-hidden="true">#</a> 采样率</h2><h2 id="采样类型" tabindex="-1"><a class="header-anchor" href="#采样类型" aria-hidden="true">#</a> 采样类型</h2>',16);function f(g,x){const s=h("DocsAD");return n(),t("div",null,[o,m,p,l(s),u,_])}const y=i(d,[["render",f],["__file","communication.html.vue"]]);export{y as default};
