import{_ as t,K as i,L as l,M as e,N as r,a5 as o,W as c,F as n}from"./framework-edebdfe1.js";const d={},h=e("h1",{id:"nervos-ckb",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#nervos-ckb","aria-hidden":"true"},"#"),r(" Nervos(CKB)")],-1),_={href:"https://www.nervos.org/",target:"_blank",rel:"noopener noreferrer"},g=c('<h2 id="_1-创立背景" tabindex="-1"><a class="header-anchor" href="#_1-创立背景" aria-hidden="true">#</a> 1. 创立背景</h2><ul><li><strong>成立时间</strong>：Nervos项目于2018年成立。</li><li><strong>创始团队</strong>：由多位区块链领域的专家和开发者组成，旨在解决现有区块链技术的局限性。</li></ul><h2 id="_2-ckb的发布" tabindex="-1"><a class="header-anchor" href="#_2-ckb的发布" aria-hidden="true">#</a> 2. CKB的发布</h2><ul><li><strong>CKB的定义</strong>：Common Knowledge Base (CKB) 是Nervos的基础层，采用分层架构。</li><li><strong>主要目标</strong>：为去中心化应用提供安全和可扩展的基础设施。</li></ul><h2 id="_3-技术发展" tabindex="-1"><a class="header-anchor" href="#_3-技术发展" aria-hidden="true">#</a> 3. 技术发展</h2><ul><li><strong>分层架构</strong>：Nervos CKB结合了Layer 1和Layer 2解决方案，以提升性能和安全性。</li><li><strong>共识机制</strong>：引入了混合共识机制，结合了工作量证明（PoW）和权益证明（PoS）。</li></ul><h2 id="_4-生态系统的扩展" tabindex="-1"><a class="header-anchor" href="#_4-生态系统的扩展" aria-hidden="true">#</a> 4. 生态系统的扩展</h2><ul><li><strong>智能合约支持</strong>：Nervos CKB支持多种编程语言（如Rust和C），使开发者能够构建智能合约。</li><li><strong>互操作性</strong>：致力于与其他区块链的互通，构建跨链生态系统。</li></ul><h2 id="_5-未来愿景" tabindex="-1"><a class="header-anchor" href="#_5-未来愿景" aria-hidden="true">#</a> 5. 未来愿景</h2><ul><li><strong>长期可持续性</strong>：Nervos致力于提供经济激励机制，确保网络的稳定与发展。</li><li><strong>推动去中心化应用</strong>：鼓励开发者在其平台上构建多样化的去中心化应用（DApps）。</li></ul><p>Nervos CKB的历史是其逐步发展和演变的缩影，反映了区块链技术的不断进步和创新。</p>',11);function u(f,p){const a=n("ExternalLinkIcon"),s=n("DocsAD");return i(),l("div",null,[h,e("p",null,[e("a",_,[r("Nervos"),o(a)]),r("网络是一个去中心化的区块链平台，致力于创建一个安全、可扩展的基础设施，支持多种应用和协议。")]),o(s),g])}const x=t(d,[["render",u],["__file","index.html.vue"]]);export{x as default};
