import{_ as o,K as s,L as c,M as n,N as e,a5 as a,W as l,F as r}from"./framework-edebdfe1.js";const h={},d=n("h1",{id:"binance-smart-chain-bsc",tabindex:"-1"},[n("a",{class:"header-anchor",href:"#binance-smart-chain-bsc","aria-hidden":"true"},"#"),e(" Binance Smart Chain(BSC)")],-1),_={href:"https://www.bnbchain.org/",target:"_blank",rel:"noopener noreferrer"},B=l('<h2 id="_2020年" tabindex="-1"><a class="header-anchor" href="#_2020年" aria-hidden="true">#</a> 2020年</h2><ul><li><strong>项目启动</strong>：2020年4月，Binance 宣布计划推出一个新的区块链网络 Binance Smart Chain (BSC)，作为 Binance Chain 的平行链。</li><li><strong>技术架构</strong>：BSC 采用了双链架构，允许用户在 Binance Chain 和 BSC 之间无缝转移资产。BSC 兼容以太坊虚拟机（EVM），使开发者能够轻松将现有的以太坊 DApp 移植到 BSC 上。</li><li><strong>主网发布</strong>：2020年9月，BSC 主网正式上线，开始支持智能合约和去中心化应用。</li></ul><h2 id="_2021年" tabindex="-1"><a class="header-anchor" href="#_2021年" aria-hidden="true">#</a> 2021年</h2><ul><li><strong>生态系统扩展</strong>：BSC 迅速吸引了大量的 DApp 开发者和用户，成为以太坊的主要竞争对手之一。许多 DeFi 项目如 PancakeSwap、Venus 和 Autofarm 在 BSC 上启动并迅速增长。</li><li><strong>用户增长</strong>：由于 BSC 提供了更低的交易费用和更快的交易速度，大量用户和交易量涌入 BSC 网络，推动了其生态系统的快速扩展。</li><li><strong>安全性改进</strong>：BSC 投资于网络安全，增强其链上治理和安全机制，以确保用户资产的安全。</li></ul><h2 id="_2022年" tabindex="-1"><a class="header-anchor" href="#_2022年" aria-hidden="true">#</a> 2022年</h2><ul><li><strong>技术升级</strong>：BSC 进行了一系列技术升级，以提高网络的性能和可扩展性，包括改进共识机制和优化网络节点。</li><li><strong>社区支持</strong>：BSC 通过各种社区激励计划和开发者扶持计划，进一步扩大其开发者社区和用户基础。</li><li><strong>跨链互操作性</strong>：BSC 加强了与其他区块链的互操作性，支持更多的跨链桥接和资产转移，推动区块链生态系统的融合发展。</li></ul>',6);function C(S,g){const t=r("ExternalLinkIcon"),i=r("DocsAD");return s(),c("div",null,[d,n("p",null,[n("a",_,[e("Binance Smart Chain (BSC)"),a(t)]),e(" 是一个由 Binance 运营的高性能区块链平台，旨在提供快速和低成本的交易，支持智能合约和去中心化应用（DApp）的开发。")]),a(i),B])}const p=o(h,[["render",C],["__file","index.html.vue"]]);export{p as default};
