import{_ as r,K as n,L as a,M as i,N as t,a5 as e,W as p,F as s}from"./framework-edebdfe1.js";const c={},m=i("h1",{id:"optimism",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#optimism","aria-hidden":"true"},"#"),t(" Optimism")],-1),h={id:"https-optimistic-etherscan-io",tabindex:"-1"},d=i("a",{class:"header-anchor",href:"#https-optimistic-etherscan-io","aria-hidden":"true"},"#",-1),_={href:"https://optimistic.etherscan.io/",target:"_blank",rel:"noopener noreferrer"},u=p('<h2 id="一、历史" tabindex="-1"><a class="header-anchor" href="#一、历史" aria-hidden="true">#</a> 一、历史</h2><ol><li><p><strong>背景</strong></p><ul><li>Optimism 是一种基于以太坊的 Layer 2 解决方案，旨在解决以太坊网络的可扩展性问题。</li><li>随着以太坊的普及，网络面临交易费用高昂和确认时间长的问题。</li></ul></li><li><p><strong>发展历程</strong></p><ul><li>2019 年，Optimism 团队开始研发该项目，旨在提供更高效的交易处理方案。</li><li>2020 年，Optimism 首次公开其技术，吸引了开发者的关注。</li><li>2021 年，Optimism 进行了一次成功的主网测试，标志着其正式投入使用。</li></ul></li></ol><h2 id="二、原理" tabindex="-1"><a class="header-anchor" href="#二、原理" aria-hidden="true">#</a> 二、原理</h2><ol><li><p><strong>Optimistic Rollups</strong></p><ul><li>Optimism 采用 Optimistic Rollups 技术，将多个交易打包并在 Layer 2 上执行，降低交易费用并提高速度。</li><li>在 Optimistic Rollups 中，所有交易默认被视为有效，只有在出现争议时才需要进行验证。</li></ul></li><li><p><strong>安全性</strong></p><ul><li>Optimism 的安全性依赖于以太坊主链，所有的交易数据最终都将记录在以太坊上。</li><li>在发生争议时，系统会使用链上数据进行验证，确保交易的有效性。</li></ul></li><li><p><strong>兼容性</strong></p><ul><li>Optimism 兼容以太坊现有的智能合约，使得开发者可以轻松将 DApp 部署到 Optimism 上。</li><li>开发者可以使用熟悉的工具（如 Remix 和 Truffle）进行开发和测试。</li></ul></li><li><p><strong>用户体验</strong></p><ul><li>Optimism 旨在提供更快、更便宜的交易体验，降低用户使用去中心化应用的门槛。</li></ul></li></ol>',4);function f(g,O){const l=s("ExternalLinkIcon"),o=s("DocsAD");return n(),a("div",null,[m,i("h2",h,[d,t(),i("a",_,[t("https://optimistic.etherscan.io/"),e(l)])]),e(o),u])}const b=r(c,[["render",f],["__file","optimism.html.vue"]]);export{b as default};
