import{_ as a,K as e,L as t,a5 as p,M as n,N as i,W as o,F as l}from"./framework-edebdfe1.js";const c={},r=n("h1",{id:"时间锁-timelock",tabindex:"-1"},[n("a",{class:"header-anchor",href:"#时间锁-timelock","aria-hidden":"true"},"#"),i(" 时间锁(Timelock)")],-1),u=n("p",null,"时间锁（Timelock）是区块链技术中的一种机制，用于延迟交易或特定操作的执行时间，直到满足特定的时间或区块高度条件。时间锁被广泛应用于智能合约、安全措施和交易延迟等场景。",-1),d=o(`<h2 id="_1-时间锁的作用" tabindex="-1"><a class="header-anchor" href="#_1-时间锁的作用" aria-hidden="true">#</a> 1. 时间锁的作用</h2><ul><li><p><strong>延迟交易执行</strong>：允许用户设置一笔交易或操作在未来的某个时间点或区块高度之后才能执行，提供灵活的资金管理。</p></li><li><p><strong>实现智能合约功能</strong>：在智能合约中，用于延迟执行某些操作或确保合约在特定条件满足后执行，如定时支付和赎回。</p></li><li><p><strong>提高安全性</strong>：在多重签名钱包或去中心化组织中，时间锁可以提供审查期，防止紧急提现或恶意操作。</p></li><li><p><strong>防止双花攻击</strong>：通过设置交易在一定数量的区块确认后才生效，减少双花攻击的风险。</p></li><li><p><strong>合约锁定期</strong>：锁定代币或资产直到特定时间或条件满足后解锁，常用于代币发行和创始团队分配。</p></li><li><p><strong>保障公平交易</strong>：在原子交换等场景中，确保双方交易的公平性，避免一方受损。</p></li></ul><h2 id="_2-时间锁的类型" tabindex="-1"><a class="header-anchor" href="#_2-时间锁的类型" aria-hidden="true">#</a> 2. 时间锁的类型</h2><ul><li><p><strong>绝对时间锁</strong>：基于具体的时间点（如 Unix 时间戳），指定交易只能在该时间点之后执行。</p></li><li><p><strong>相对时间锁</strong>：基于区块高度，指定交易只能在当前区块之后的某个区块高度时才能执行。</p></li></ul><h2 id="_3-时间锁的应用场景" tabindex="-1"><a class="header-anchor" href="#_3-时间锁的应用场景" aria-hidden="true">#</a> 3. 时间锁的应用场景</h2><ul><li><p><strong>智能合约</strong>：确保合约条款在规定时间内执行，如贷款合约中的还款期。</p></li><li><p><strong>多重签名钱包</strong>：提供撤销恶意交易的冷静期，增强资金的安全性。</p></li><li><p><strong>代币发行与锁仓</strong>：锁定创始团队的代币，防止短期抛售对市场造成冲击。</p></li><li><p><strong>原子交换</strong>：与哈希锁（Hashlock）结合使用，确保跨链交易的安全和公平。</p></li></ul><div class="language-solidity line-numbers-mode" data-ext="solidity"><pre class="language-solidity"><code><span class="token comment">// SPDX-License-Identifier: MIT</span>

<span class="token keyword">pragma</span> <span class="token keyword">solidity</span> <span class="token operator">^</span><span class="token version number">0.8.0</span><span class="token punctuation">;</span>

<span class="token keyword">contract</span> <span class="token class-name">Timelock</span> <span class="token punctuation">{</span>
    <span class="token builtin">uint</span> <span class="token keyword">public</span> releaseTime<span class="token punctuation">;</span>
    <span class="token builtin">address</span> <span class="token keyword">payable</span> <span class="token keyword">public</span> beneficiary<span class="token punctuation">;</span>

    <span class="token keyword">constructor</span><span class="token punctuation">(</span><span class="token builtin">uint</span> _releaseTime<span class="token punctuation">,</span> <span class="token builtin">address</span> <span class="token keyword">payable</span> _beneficiary<span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token keyword">require</span><span class="token punctuation">(</span>_releaseTime <span class="token operator">&gt;</span> block<span class="token punctuation">.</span>timestamp<span class="token punctuation">,</span> <span class="token string">&quot;Release time must be in the future&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        releaseTime <span class="token operator">=</span> _releaseTime<span class="token punctuation">;</span>
        beneficiary <span class="token operator">=</span> _beneficiary<span class="token punctuation">;</span>
    <span class="token punctuation">}</span>

    <span class="token keyword">function</span> <span class="token function">release</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token keyword">public</span> <span class="token punctuation">{</span>
        <span class="token keyword">require</span><span class="token punctuation">(</span>block<span class="token punctuation">.</span>timestamp <span class="token operator">&gt;=</span> releaseTime<span class="token punctuation">,</span> <span class="token string">&quot;Current time is before release time&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token keyword">require</span><span class="token punctuation">(</span>msg<span class="token punctuation">.</span>sender <span class="token operator">==</span> beneficiary<span class="token punctuation">,</span> <span class="token string">&quot;Only beneficiary can release funds&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

        beneficiary<span class="token punctuation">.</span><span class="token function">transfer</span><span class="token punctuation">(</span><span class="token builtin">address</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">)</span><span class="token punctuation">.</span>balance<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>

    <span class="token function">receive</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token keyword">external</span> <span class="token keyword">payable</span> <span class="token punctuation">{</span><span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div>`,7);function k(m,v){const s=l("DocsAD");return e(),t("div",null,[r,u,p(s),d])}const g=a(c,[["render",k],["__file","timelock.html.vue"]]);export{g as default};
