import{_ as u,K as n,L as c,a5 as m,M as t,N as e,W as s,F as i}from"./framework-edebdfe1.js";const o={},r=t("h1",{id:"utm",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#utm","aria-hidden":"true"},"#"),e(" UTM")],-1),l=t("h2",{id:"什么是utm",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#什么是utm","aria-hidden":"true"},"#"),e(" 什么是UTM")],-1),_=t("p",null,"UTM (URL tracking mark) 指自定义链接跟踪标识，是用于跟踪链接来源、媒介、关键词、内容、名称等信息的标识。可以采集和统计对话着陆页的 UTM 信息，只需在广告投放或其他第三方跳转过程中，将 UTM 参数添加到网址中。",-1),d=s('<h2 id="utm-字段说明" tabindex="-1"><a class="header-anchor" href="#utm-字段说明" aria-hidden="true">#</a> UTM 字段说明</h2><ul><li>utm_source</li></ul><p>标识带来流量的广告主、网站、出版物等。</p><p>示例：utm_source=test</p><ul><li>utm_medium</li></ul><p>广告媒介或营销媒介，例如：每次点击费用、横幅广告和电子邮件简报。</p><p>示例：utm_medium=test</p><ul><li>utm_term</li></ul><p>标识付费搜索关键字。如果采用了人工方式标记付费关键字广告系列，那么还应使用 utm_term 来指定相应关键字。</p><p>示例：utm_term=meiqia</p><ul><li>utm_content</li></ul><p>用于区分相似内容或同一广告内的链接。例如，如果在广告平台中不同位置使用了两个相同目标的链接，就可以使用 utm_content 并为每个链接设置不同的值，以便判断哪个版本的效果更好。</p><p>示例：utm_content=test1 或 utm_content=test2</p><ul><li>utm_campaign</li></ul><p>特定的产品推广活动或战略性广告系列，如广告系列名称、标语、促销代码等。</p><p>示例：utm_campaign=test</p>',16);function p(h,f){const a=i("DocsAD");return n(),c("div",null,[r,l,_,m(a),d])}const M=u(o,[["render",p],["__file","utm.html.vue"]]);export{M as default};
