import{_ as t,K as d,L as o,M as a,N as e,a5 as r,W as n,F as h}from"./framework-edebdfe1.js";const l={},c=a("h1",{id:"zcash-zec",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#zcash-zec","aria-hidden":"true"},"#"),e(" ZCash(ZEC)")],-1),u={href:"https://z.cash",target:"_blank",rel:"noopener noreferrer"},_=n('<h3 id="_2013-年" tabindex="-1"><a class="header-anchor" href="#_2013-年" aria-hidden="true">#</a> 2013 年</h3><ul><li><strong>项目启动</strong>：Zcash 的概念由 Zooko Wilcox 及其团队提出，旨在创建一个能保护用户隐私的数字货币。</li></ul><h3 id="_2016-年" tabindex="-1"><a class="header-anchor" href="#_2016-年" aria-hidden="true">#</a> 2016 年</h3><ul><li><strong>正式发布</strong>：Zcash 于 10 月 28 日正式上线，首次采用了 zk-SNARKs（零知识简洁非交互式论证），允许交易的金额和参与者保持隐私。</li></ul><h3 id="_2018-年" tabindex="-1"><a class="header-anchor" href="#_2018-年" aria-hidden="true">#</a> 2018 年</h3><ul><li><strong>扩展功能</strong>：引入了透明地址（T-address）和隐私地址（Z-address），用户可以选择是否公开交易信息。</li></ul><h3 id="_2019-年" tabindex="-1"><a class="header-anchor" href="#_2019-年" aria-hidden="true">#</a> 2019 年</h3><ul><li><strong>协议升级</strong>：Zcash 进行了重大升级，改善了网络的安全性和性能，增加了对隐私保护的支持。</li></ul><h3 id="_2020-年" tabindex="-1"><a class="header-anchor" href="#_2020-年" aria-hidden="true">#</a> 2020 年</h3><ul><li><strong>减半事件</strong>：Zcash 的区块奖励减半，从 12.5 ZEC 减少到 6.25 ZEC，遵循比特币的减半模式。</li></ul><h3 id="_2021-年" tabindex="-1"><a class="header-anchor" href="#_2021-年" aria-hidden="true">#</a> 2021 年</h3><ul><li><strong>生态系统发展</strong>：随着 DeFi（去中心化金融）和 NFT（非同质化代币）概念的兴起，Zcash 开始探索与这些领域的整合。</li></ul>',12),g={id:"zcash原理",tabindex:"-1"},f=a("a",{class:"header-anchor",href:"#zcash原理","aria-hidden":"true"},"#",-1),x={href:"https://zcash.readthedocs.io/",target:"_blank",rel:"noopener noreferrer"},Z=n('<h3 id="zk-snarks" tabindex="-1"><a class="header-anchor" href="#zk-snarks" aria-hidden="true">#</a> zk-SNARKs</h3><ul><li><strong>零知识证明</strong>：Zcash 使用 zk-SNARKs（零知识简洁非交互式论证）技术，允许交易双方在不透露任何具体信息的情况下验证交易的有效性。</li><li><strong>隐私保护</strong>：这种技术使得用户可以证明他们拥有足够的资金进行交易，而无需公开余额或交易金额。</li></ul><h3 id="地址类型" tabindex="-1"><a class="header-anchor" href="#地址类型" aria-hidden="true">#</a> 地址类型</h3><ul><li><strong>透明地址（T-address）</strong>：类似于比特币地址UTXO，交易信息对所有人可见。</li><li><strong>隐私地址（Z-address）</strong>：使用 zk-SNARKs 保护交易信息，只有交易双方知道金额和地址。</li></ul><h3 id="交易结构" tabindex="-1"><a class="header-anchor" href="#交易结构" aria-hidden="true">#</a> 交易结构</h3><ul><li><strong>Shielded Transactions</strong>：通过使用隐私地址进行的交易，确保所有相关信息都被加密。</li><li><strong>Unshielded Transactions</strong>：在透明地址之间进行的交易，允许公开查看。</li></ul><h3 id="区块链" tabindex="-1"><a class="header-anchor" href="#区块链" aria-hidden="true">#</a> 区块链</h3><ul><li>Zcash 的区块链与比特币类似，采用工作量证明机制（PoW）来验证和记录交易。</li><li><strong>矿工</strong>：通过解决复杂的数学问题来创建新区块，并获得 ZEC 奖励。</li></ul><h3 id="资金供应" tabindex="-1"><a class="header-anchor" href="#资金供应" aria-hidden="true">#</a> 资金供应</h3><ul><li>Zcash 的发行机制与比特币相似，设定总供应量为 2100 万 ZEC，奖励每隔 4 年减半。</li></ul><h2 id="安全性" tabindex="-1"><a class="header-anchor" href="#安全性" aria-hidden="true">#</a> 安全性</h2><ul><li>Zcash 通过密码学技术保护用户的交易隐私，同时也确保网络的安全性。</li><li><strong>审计和合规</strong>：Zcash 在保持隐私的同时，允许合规审计，以满足监管要求。</li></ul>',12);function b(z,k){const s=h("ExternalLinkIcon"),i=h("DocsAD");return d(),o("div",null,[c,a("p",null,[a("a",u,[e("Zcash"),r(s)]),e(" 是一种注重隐私的加密货币，于 2016 年推出。它基于比特币的代码，但采用了不同的技术，以提供增强的隐私保护。")]),r(i),_,a("h2",g,[f,e(),a("a",x,[e("ZCash原理"),r(s)])]),Z])}const N=t(l,[["render",b],["__file","zcash.html.vue"]]);export{N as default};
